mapper.select:
select count(1)
from t_account
where corp_id = #{corpId,jdbcType=INTEGER}

mapper.insert:
insert into t_account (id, corp_id, name, 
  password, mobile_phone, email, 
  create_date, update_date, biz_module_key
  )
values (#{id,jdbcType=INTEGER}, #{corpId,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, 
  #{password,jdbcType=VARCHAR}, #{mobilePhone,jdbcType=VARCHAR}, #{email,jdbcType=VARCHAR}, 
  #{createDate,jdbcType=TIMESTAMP}, #{updateDate,jdbcType=TIMESTAMP}, #{bizModuleKey,jdbcType=VARCHAR}
  )
