mapper.select:
select 

from t_resource
where db_id = #{dbId,jdbcType=INTEGER}
  and corp_id = #{corpId,jdbcType=INTEGER}

mapper.delete:
delete from t_resource
where db_id = #{dbId,jdbcType=INTEGER}
  and corp_id = #{corpId,jdbcType=INTEGER}

mapper.insert:
insert into t_resource (db_id, corp_id, text, 
  parent_id, name, menu_url, 
  menu_type, icon_cls, is_leaf, 
  js_path, valid_status, role_key
  )
values (#{dbId,jdbcType=INTEGER}, #{corpId,jdbcType=INTEGER}, #{text,jdbcType=VARCHAR}, 
  #{parentId,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{menuUrl,jdbcType=VARCHAR}, 
  #{menuType,jdbcType=VARCHAR}, #{iconCls,jdbcType=VARCHAR}, #{isLeaf,jdbcType=INTEGER}, 
  #{jsPath,jdbcType=VARCHAR}, #{validStatus,jdbcType=INTEGER}, #{roleKey,jdbcType=VARCHAR}
  )
